angular.module("SetMovieNight").controller("MovieSearchController",["$scope","$location","SelectCelebs","$timeout",function($scope,$location,SelectCelebs,$timeout){$scope.movies=SelectCelebs.getMoviesResult();$scope.onSelect=function($item,$model,$label){$location.path("/moviesDetails/"+$item.id)};$scope.getMovieList=function(title){SelectCelebs.findMovieByAjax(title).then(function(response){$scope.movies=response.data.results;SelectCelebs.setMoviesResult($scope.movies);$scope.isLoading=false})};$scope.$watch("movieSelected",function(newNames,oldNames){if(newNames){$timeout(function(){$scope.getMovieList($scope.movieSelected);$scope.isLoading=true},2000)}})}]);